import React ,{useState} from 'react'
import { Link, useNavigate } from 'react-router-dom'
import {signInWithEmailAndPassword,updateProfile} from "firebase/auth";
import InputControl from '../InputControl/InputControl'
import styles from "../Login/login.module.css"
import { auth } from '../../firebase';
const Login = () => {
  const navigate = useNavigate();
  const [values, setValues] = useState({
    email: "",
    pass: "",
  });

  const [errorMsg, setErrorMsg] = useState("");
  const [submitButtonDisabled, setSubmitButtonDisabled] = useState(false);

  const handleSubmission = () => {
    if ( !values.email || !values.pass) {
      setErrorMsg("Fill all Fields");
      return;
    }
    setErrorMsg("");
    setSubmitButtonDisabled(true);
    signInWithEmailAndPassword(auth, values.email, values.pass).then(async (res) => {
      setSubmitButtonDisabled(false);
      // const user = res.user;
      // console.log(user);
      // await updateProfile(user, {
      //   displayName: values.name,
      // });
      navigate("/");

      // console.log(res);
    })
      .catch((err) => {
        setSubmitButtonDisabled(false);
        setErrorMsg(err.message)
      })
    console.log(values);
  }
    return (
      <div className={styles.container}>
        <div className={styles.innerBox}>
          <h1 className={styles.heading}>Login</h1>
          <InputControl 
          label="Email"
          placeholder="Enter your email"
          onChange={event=>setValues((prev)=>({...prev,email:event.target.value}))}
           />
          <InputControl 
          label="Password" 
          placeholder="Enter Password" 
          onChange={event=>setValues((prev)=>({...prev,pass:event.target.value}))}
          />

          <div className={styles.footer}>
            <b className={styles.error}>{errorMsg}</b>
            <button disabled={submitButtonDisabled} onClick={handleSubmission}>Login</button>
            <p>
              Already have an account? <span><Link to="/signup">SignUp</Link></span>
            </p>
          </div>
        </div>
      </div>
    )
  }

  export default Login
